import React, { useState } from 'react';
import Tweet from "./Tweet";



function App() {
  const [users, setUser] = useState([
    { name: "Jason", message: "Hello World" },
    { name: "John", message: "I am John" }
  ]);

  return(
    <div className="app">
      {users.map(user => (
        <Tweet name={user.name} message={user.message}/>
      ))}
    </div>
  );
};


export default App;
